import React, { useCallback, useEffect,useRef,useState} from "react"
import { useParams } from "react-router-dom"
import { select_emp_name_id } from "../api"
import { Loading } from "./loading"



export     function render_display(startidx,endidx,now_item,itemOffset){
    const content = [];
    for(let i=startidx;i<=endidx;++i){
        var Height=''
        var Zindex=0
        var Bgcolor=''
        var Boxshadow=''
        var Color=''
        var Top=i * itemSize+'px'
        if(now_item===i){

            Height=''
            Zindex=15
            Bgcolor='rgb(255,255,255)'
            Boxshadow='2px 2px 10px rgb(112,128,144)'
            Color='rgb(0,0,0)'
        }
        else{
            Height=itemSize+'px'
            Zindex=''
            Bgcolor=''
            Boxshadow=''
            Color=''
        }
        if(i>now_item){
            console.log(item)
            Top=itemOffset+i * itemSize+'px'
        }

        content.push({
            index:i,
            Style:
            {
                boxShadow:Boxshadow,
                height:Height,
                backgroundColor:Bgcolor,
                zIndex:Zindex,
                color:Color,

                position: "absolute",

                left: 0,
                right: 0,
                top: Top
            }
        })



    }
    window.sessionStorage.setItem('style_arr',JSON.stringify(content))

}
const itemSize=30
const bufferSize=20
const screenHeight=500
const visibleCount=screenHeight/itemSize


export function Return_component({number,page,arr_params}){

    const ele=useRef(null)

    const arr=arr_params[page]
    const {article_page}=useParams()


    const arrHeight=itemSize*arr.length

    const [now_item,set_now_item]=useState()

    const [startIndex,setStartIndex]=useState(0)
    const [endIndex,setEndIndex]=useState(visibleCount)
    const [offset,setoffset]=useState(0)



    const [itemOffset,setItemoffset]=useState(0)

    useEffect(()=>{

        setoffset(startIndex*30)
    },[startIndex])



    function onScroll(e){
        var Top=ele.current.scrollTop
        var startidx=Math.floor(Top/itemSize)
        var endidx=startidx+visibleCount
        var startoffset=Top - (Top % itemSize);

        console.log(startIndex,endIndex,offset)

        let originStartIdx=startidx

        setStartIndex(Math.max(originStartIdx - bufferSize, 0))
        setEndIndex(Math.min(
            originStartIdx + visibleCount + bufferSize,
            arr.length - 1
        ))
        render_display(startIndex,endIndex,now_item,itemOffset)
    };  
        return(                
            <>

                <div  ref={ele} 
                    onScroll={onScroll} 
                    style={(arr&&arrHeight>screenHeight)||(arr&&arr.length===0)
                        ?{height:`${screenHeight}px`}
                        :{height:`${0}px`}}
                    className="all_employee">

                <div style={{height:`${arrHeight}px`}} 
                    className="employee">

                    <div 

                        className="employee_container">

                                {
                                    arr instanceof Array && arr.length>0 &&window.sessionStorage.getItem('style_arr')
                                    ? arr.slice(startIndex,endIndex).map((item,index)=>{

                                        render_display(startIndex,endIndex,now_item,itemOffset)
                                        var style_arr=JSON.parse(window.sessionStorage.getItem('style_arr'))[index]


                                        return(
                                            <>
                                            <Id_name 
                                            setItemoffset={setItemoffset}
                                            item_index={style_arr.index}
                                            style={style_arr.Style}
                                            startIndex={startIndex}
                                            set={set_now_item}
                                            now_item={now_item}
                                            component_state={number}
                                            key={style_arr.index}
                                            item={item} 
                                            index={index} />
                                            </>
                                        )
                                    })
                                    :<Loading/>
                                }
                    </div>
                </div>
                </div>

            </>)
}

export function Id_name({set,now_item,item,index,component_state,style,startIndex,item_index,setItemoffset,Top}){
    const rowitem_ref=useRef(null)

        if(component_state===1){
            return(
                <>

                            <div ref={rowitem_ref} 
                                className="row_container" 
                                onClick={(e)=>{

                                    set(item_index)
                                    var timer=setInterval(() => {
                                        setItemoffset(e.target.clientHeight)
                                        clearInterval(timer)
                                    },100 );


                                   }}
                                        style={style}>
                                <div style={{width:'2vw'}}><div>{item[0]}</div></div> 
                                <div style={{width:'5vw'}}><div>{item[1]}</div></div>
                                <div style={{width:'40vw',overflow:'hidden',height:style.height}}>{item[2]}</div>
                                <input style={{width:'2vw'}} placeholder={item[4]} type='text'/>
                                <input style={{width:'2vw'}} placeholder={item[5]} type='text'/>
                                <input style={{width:'2vw'}} placeholder={item[6]} type='text'/>
                                <input style={{width:'2vw'}} placeholder={item[7]} type='text'/>
                                <input style={{width:'2vw'}} placeholder={item[8]} type='text'/>
                                <input style={{width:'4vw'}} placeholder={item[9]} type='text'/>
                                <textarea defaultValue={item[10]} maxLength={50} style={{width:'20vw'}}></textarea>
                                <input style={{width:'2vw'}} placeholder={item[11]} type='text'/>
                                <input style={{width:'2vw'}} vplaceholder={item[12]} type='text'/>

                            </div>

                </>
            )
        }
        else if(component_state===2){
            return(
                <>
                <input placeholder={item[0]}  type='text'/>
                <input placeholder={item[1]} type='text'/>
                <input placeholder={item[2]} type='text'/>
                <input placeholder={item[3]} type='text'/><br/>
                </>
            )

        }
        else{
            return(
                <>
                <input placeholder={item[0]}  type='text'/>
                <input placeholder={item[1]} type='text'/><br/>
                </>
            )

        }


}
