export function admin_sidebar(){
    const admin_sidebar_orignal=['組別管理','主管','離職員工','新增年度'];
    var banch=window.sessionStorage.getItem('all_banch')
    if(banch===null||banch===undefined)
    for(let item=2;item<banch.length;item++){
        admin_sidebar_orignal.splice(item,0,item)
    }
    return  admin_sidebar_orignal

}
export function manager_sidebar(){
    var banch=window.sessionStorage.getItem('banch');
