export function select_emp_name_id(number){

    const emp_arr=[]
    if(number===1){
        for(let i=0;i<40;i++){
            emp_arr.push([i,'name: '+i,'goal'+i,'attitude','efficiency','professional',2000+i,'directions'+i,i,0,0])
