import React, { useCallback, useEffect,useRef,useState} from "react"
import { useNavigate, useParams } from "react-router-dom"


import { fixed_title,fixed_title_emp } from "../api"





export function Return_component({number,page,arr_params,new_emp}){

    const itemSize=30
    const bufferSize=20
    var screenHeight=0
    number===5
    ?screenHeight=200
    :screenHeight=500
    const visibleCount=screenHeight/itemSize
    const fixed_title_size=40

    const ele=useRef(null)

    const arr=arr_params
    const {article_page}=useParams()


    const arrHeight=itemSize*arr.length

    const [now_item,set_now_item]=useState()


    const [startIndex,setStartIndex]=useState(0)
    const [endIndex,setEndIndex]=useState(visibleCount)
    const [offset,setoffset]=useState(0)

    const [itemOffset,setItemoffset]=useState(0)

    useEffect(()=>{

        setoffset(startIndex*30)
    },[startIndex])
    useEffect(()=>{
        setStartIndex(0)
        setEndIndex(Math.min(
            visibleCount + bufferSize,
            arr.length
        ))
    },[arr,page])
    function onScroll(e){
        var Top=ele.current.scrollTop
        var startidx=Math.floor(Top/itemSize)
        var endidx=startidx+visibleCount
        var startoffset=Top - (Top % itemSize);

        console.log(startIndex,endIndex,offset)

        let originStartIdx=startidx

        setStartIndex(Math.max(originStartIdx - bufferSize, 0))
        setEndIndex(Math.min(
            originStartIdx + visibleCount + bufferSize,
            arr.length
        ))
        console.log(arr.length)

    };  

    function render_display(startidx,endidx,now_item,itemOffset){
        const content = [];
        for(let i=startidx;i<=endidx;++i){
            var Height=''
            var Zindex=0
            var Bgcolor=''
            var Boxshadow=''
            var Color=''
            var Top=i * itemSize+fixed_title_size+'px'
            if(now_item===i){

                Height=''
                Zindex=15
                Bgcolor='rgb(255,255,255)'
                Boxshadow='2px 2px 10px rgb(112,128,144)'
                Color='rgb(0,0,0)'
            }
            else{
                Height=itemSize+'px'
                Zindex=''
                Bgcolor=''
                Boxshadow=''
                Color=''
            }
            if(i>now_item){

                Top=itemOffset+i * itemSize+fixed_title_size+'px'
            }

            content.push({
                index:i,
                Style:
                {
                    transition:'0.3s',
                    boxShadow:Boxshadow,
                    height:Height,
                    backgroundColor:Bgcolor,
                    zIndex:Zindex,
                    color:Color,


                    position: "absolute",

                    left: 0,
                    right: 0,
                    top: Top
                }
            })
        }
        return content
    }
    return(                
        <div  
            ref={ele} 
            onScroll={onScroll} 
            style={(arr&&arrHeight>screenHeight)||(arr&&arr.length===0)
                    ?{height:`${screenHeight}px`}
                    :{height:`${screenHeight}px`}}
            className="all_employee">
        <div 
            style={article_page!=='每月考核績效'&&page!=='每月考核績效'
                    ?{height:`${arrHeight+itemOffset+fixed_title_size}px`,width:'100%'}
                    :{height:`${arrHeight+itemOffset+fixed_title_size}px`,width:'900px'}}
            className="employee">
            <Fixed_title component_state={number}/>
            <div className="employee_container">
                {
                    arr instanceof Array && arr.length>0
                    ? arr.slice(startIndex,endIndex).map((item,index)=>{
                        var style_content=render_display(startIndex,endIndex,now_item,itemOffset)
                        var style_arr=style_content[index]
                        return(
                            <Id_name 
                                new_emp={new_emp}
                                setItemoffset={setItemoffset}
                                item_index={style_arr.index}
                                style={style_arr.Style}
                                startIndex={startIndex}
                                set={set_now_item}
                                now_item={now_item}
                                component_state={number}
                                key={style_arr.index}
                                item={item} 
                                index={index} />
                            )
                    })
                    :<></>
                }
                </div>
            </div>
        </div>
    )
}

export function Id_name({set,now_item,item,index,component_state,style,new_emp,startIndex,item_index,setItemoffset,Top}){
    const rowitem_ref=useRef(null)
    const timeout=useRef(null)
    const {page}=useParams()
    var data=JSON.parse(window.sessionStorage.getItem('banch_index'))
    const {navigate}=useNavigate()
    var permession=window.sessionStorage.getItem('permession')

    const [value,set_value]=useState(
        {
            id:'',
            name:'',
            group:'',
            account:'',
            password:'',
            onWorkDay:''
        }
    )
    useEffect(()=>{
        if(component_state===4){
            set_value(        {
                id:item[4],
                name:item[2],
                group:item[3],
                account:item[0],
                password:item[1],
                onWorkDay:item[5]
            })
        }
    },[])
    useEffect(()=>{
        timeout.current= setTimeout(() => {
            console.log('useeffect',value.account)
            var data= search(data[page],item,obj_to_arr(value))

            window.sessionStorage.setItem('banch_index',JSON.stringify(data))
            console.log('auto save success')

        }, 3000)
    },[value])

    function autoSave(e){
        var values =e.target.value
        var name=e.target.name
        clearTimeout(timeout.current)
        if(component_state===4){
            if(name==='id'){
                set_value({...value,id:values})
            }
            else if(name==='name'){
                set_value({...value,name:values})
            }
            else if(name==='group'){
                set_value({...value,group:values})
            }
            else if(name==='account'){
                set_value({...value,account:values})
            }
            else if(name==='password'){
                set_value({...value,password:values})
            }
            else if(name==='onWorkDay'){
                set_value({...value,onWorkDay:values})
            }
        }
    }
    function get_clientHeight(){
        set(item_index)
        if(rowitem_ref.current.clientHeight){
            var timer=setInterval(() => {
                setItemoffset(rowitem_ref.current.clientHeight)
                clearInterval(timer)
            },100);
        }
    }



    if(component_state===1){
        return(
            <div ref={rowitem_ref}
                className="row_container" 
                onClick={get_clientHeight}
                style={style}>
                <div style={{width:'2vw'}}>{item[0]}</div>
                <div style={{width:'5vw',overflow:'hidden',height:style.height}}>{item[1]}</div>
                <div contenteditable='true'
                    onInput={()=>{get_clientHeight()}}
                    style={{width:'30vw',overflow:'hidden',height:style.height}}><div>{item[2]}</div></div>
                <input style={{width:'2vw'}} placeholder={item[4]} type='text'/>
                <input style={{width:'2vw'}} placeholder={item[5]} type='text'/>
                <input style={{width:'2vw'}} placeholder={item[6]} type='text'/>
                <input style={{width:'2vw'}} placeholder={item[7]} type='text'/>
                <input style={{width:'2vw'}} placeholder={item[8]} type='text'/>
                <input style={{width:'4vw'}} placeholder={item[9]} type='text'/>
                <div contenteditable='true'
                    onInput={()=>{get_clientHeight()}} style={{width:'20vw',overflow:'hidden',height:style.height}}>{item[10]}</div>
                <input style={{width:'2vw'}} placeholder={item[11]} type='text'/>
                <input style={{width:'2vw'}} placeholder={item[12]} type='text'/>          
            </div>
            )
    }
    else if(component_state===2){
        return(
            <div ref={rowitem_ref} 
                className="row_container2" 
                onClick={get_clientHeight}
                style={style}>
                <div style={{width:'2vw'}}><div>{item[0]}</div></div> 
                <div style={{width:'5vw',overflow:'hidden',height:style.height}}><div>{item[1]}</div></div>
                {/* 
                <div style={{width:'30vw',overflow:'hidden',height:style.height}}>{item[2]}</div>
                */}
                <input style={{width:'2vw'}} disabled placeholder={item[3]} type='text'/>
                <input style={{width:'2vw'}} disabled placeholder={item[4]} type='text'/>
            </div>
        )
    }
    else if(component_state===3){
        return(
            <div ref={rowitem_ref} 
                className="row_container2" 
                onClick={get_clientHeight}
                style={style}>
                <input style={{width:'10vw'}} placeholder={item[0]} type='text'/>
                <input style={{width:'10vw',overflow:'hidden',height:style.height}} placeholder={item[1]} type='text'/>
            </div>
        )
    }
    else if(component_state===4){
        return(
            <div ref={rowitem_ref} 
                className="row_container" 
                onClick={get_clientHeight}
                style={style}>
                <div><input style={{width:'10vw'}} onChange={autoSave} name={'id'} value={value.id} type='text'/></div>
                <div><input 
                            style={{width:'10vw',overflow:'hidden',height:style.height}} onChange={autoSave} name={'name'} value={value.name} type='text'/></div>
                <div><input style={{width:'10vw'}} onChange={autoSave} name={'group'} value={value.group} type='text'/></div>
                <div><input style={{width:'10vw'}} onChange={autoSave} name={'account'} value={value.account} type='text'/></div>
                <div><input style={{width:'10vw'}} onChange={autoSave} name={'password'} value={value.password} type='text'/></div>
                <div><input style={{width:'10vw'}} onChange={autoSave} name={'onWorkDay'} value={value.onWorkDay} type='text'/></div>
            </div>
        )
    }else if(component_state===5){
        return(
            <div ref={rowitem_ref} 
                className="row_container2" 
                onClick={get_clientHeight}
                style={style}>
                    <div style={{width:'10%'}}>{item[4]}</div>
                    <div style={{width:'10%'}}>{item[2]}</div>
                    <div style={{width:'10%'}}><input name={item[2]} type='radio' defaultChecked/></div>
                    <div style={{width:'10%'}}><input name={item[2]} type='radio'/></div>
            </div>
        )

    }
}
export function Fixed_title({component_state}){
    if(component_state===1){
        return(
             <div className={'row_container'} id={'fixed_title'}>
                <div style={{width:'2vw'}}>{fixed_title[0]}</div> 
                <div style={{width:'5vw'}}>{fixed_title[1]}</div>
                <div style={{width:'30vw',overflow:'hidden'}}>{fixed_title[2]}</div>

                <input style={{width:'2vw'}} disabled placeholder={fixed_title[3]} type='text'/>
                <input style={{width:'2vw'}} disabled placeholder={fixed_title[4]} type='text'/>
                <input style={{width:'2vw'}} disabled placeholder={fixed_title[5]} type='text'/>
                <input style={{width:'2vw'}} disabled placeholder={fixed_title[6]} type='text'/>
                <input style={{width:'2vw'}} disabled placeholder={fixed_title[7]} type='text'/>
                <input style={{width:'4vw'}} disabled placeholder={fixed_title[8]} type='text'/>
                <div style={{width:'20vw'}}>{fixed_title[9]}</div>
                <input style={{width:'2vw'}} disabled placeholder={fixed_title[10]} type='text'/>
                <input style={{width:'2vw'}}disabled placeholder={fixed_title[11]} type='text'/>
            </div>    
        )
    }
    else if(component_state===2){
        return(
            <div  className={'row_container2'} id={'fixed_title'}>
                <div style={{width:'2vw'}}><div>{fixed_title[0]}</div></div> 
                <div style={{width:'5vw'}}><div>{fixed_title[1]}</div></div>
                {/* 
                <div style={{width:'30vw',overflow:'hidden'}}>{fixed_title[2]}</div>
                */}
                <input style={{width:'2vw'}} disabled placeholder={fixed_title[3]} type='text'/>
                <input style={{width:'2vw'}} disabled placeholder={fixed_title[12]} type='text'/>
            </div>    
        )
    }
    else if(component_state===3){
        return(
            <div className={'row_container2'} id={'fixed_title'}>
                <div style={{width:'10vw'}}><div>{fixed_title[0]}</div></div> 
                <div style={{width:'10vw'}}><div>{fixed_title[1]}</div></div>
            </div>
        )
    }
    else if(component_state===4){
        return(
            <div className={'row_container'} id={'fixed_title'}>
                <div style={{width:'10vw'}}><div>{fixed_title_emp[0]}</div></div> 
                <div style={{width:'10vw'}}><div>{fixed_title_emp[1]}</div></div>
                <div style={{width:'10vw'}}><div>{fixed_title_emp[2]}</div></div>
                <div style={{width:'10vw'}}><div>{fixed_title_emp[3]}</div></div>
                <div style={{width:'10vw'}}><div>{fixed_title_emp[4]}</div></div>
                <div style={{width:'10vw'}}><div>{fixed_title_emp[5]}</div></div>
            </div>
        )
    }
    else if(component_state===5){
        return(
            <div className={'row_container2'} id={'fixed_title'}>
                <div style={{width:'10%'}}><div>{fixed_title_emp[0]}</div></div> 
                <div style={{width:'10%'}}><div>{fixed_title_emp[1]}</div></div>
                <div style={{width:'10%'}}>新增</div>
                <div style={{width:'10%'}}>不新增</div>
            </div>
        )

    }
}
export function obj_to_arr(obj){
    var a=Object.values(obj).map((v)=>{
        return v
    })
    console.log(a)
    return a
}
export function search(arr,tar,){
    var len=0
    for(let data=0;data<arr.length;data++){
        //console.log('search_function',arr[data],tar)
        len=0
        for(let i=0;i<arr[data].length;i++){

            if(arr[data][i]===tar[i]){
                len=len+1
                console.log(arr[data][i],tar[i],len)
            }
            if(len===arr[data].length){
                arr.splice(data,1,)
                return true
            }
        }
    }
    return false
}
