import React, { useEffect,useRef,useState} from "react"
import { useParams } from "react-router-dom"
import { select_emp_name_id } from "../api"
import { Loading } from "./loading"



export function virtual_table(arr,itemSize,screenHeight,scrollTop){


}


export function Return_component({number,page,arr_params}){
    const arr=arr_params[page]
    const ele=useRef(null)
    const {article_page}=useParams()
    const itemSize=2
    const screenHeight=20

    const [now_item,set_now_item]=useState()
    const [scrollTop,setScrollTop]=useState(0)
    const [arrHeight,setArrHeight]=useState()
    const [visibleCount,setVisibleCount]=useState()
    const [startIndex,setStartIndex]=useState(0)
    const [endIndex,setEndIndex]=useState()
    const [visibleData,setVirtualData]=useState([])



    function onScroll(e){
        var scrollTop=ele.current.scrollTop
        var startidx=Math.floor(scrollTop/itemSize)
        var endidx=startid+visibleCount
        if(startIndex!==startidx){
            setScrollTop(scrollTop)

            setStartIndex(startidx)
            setEndIndex(endidx)
            setVirtualData(arr.slice(startidx,endidx))
            console.log(itemSize*arr.length,startidx,endidx)
        }

    };


    useEffect(()=>{
        var visible=Math.ceil(screenHeight/itemSize)
        var startidx=Math.floor(scrollTop/itemSize)
        var endidx=startIndex+visible

        setArrHeight(itemSize*arr.length)
        setVisibleCount(visible)
        setStartIndex(startidx)
        setEndIndex(endidx)
        setVirtualData(arr.slice(startidx,endidx))
        console.log(itemSize*arr.length,startidx,endidx)

    },[])

    if(visibleData){
        return(                
            <>
            <div ref={ele} onScroll={onScroll} className="all_employee">
                <div style={{minHeight:`${arrHeight}vh`}} className="employee">
                        <table >
                            <thead>
                                <tr>
                                    <th>編號</th>
                                    <th>姓名</th>
                                    <th>年度目標</th>
                                    <th>年度</th>
                                    <th>月份</th>
                                    <th>態度</th>
                                    <th>效率</th>
                                    <th>專業</th>
                                    <th>績效獎金</th>
                                    <th>分數說明</th>
                                    <th>遲到</th>
                                    <th>未依規定請假</th>

                                </tr>
                            </thead>
                            <tbody onScroll={onScroll}>

                                {

                                    visibleData.map((item,index)=>{
                                        return(
                                            <>
                                            <Id_name 
                                            set={set_now_item}
                                            now_item={now_item}
                                            component_state={number} 
                                            key={index} item={item} 
                                            index={index} />
                                            </>
                                        )
                                    })
                                }

                            </tbody>
                        </table>
            </div>
            </div>
            </>)
    }


}

export function Id_name({set,now_item,item,index,component_state}){
    const [height,set_height]=useState('2vh')

    useEffect(()=>{
        if(now_item===index){
            set_height('')
        }
        else{
            set_height('4vh')
        }
    },[now_item])
        if(component_state===1){
            return(
                <>
                        <tr onClick={()=>{
                            set(index)
                        }}>
                            <td><div style={{width:'1vw'}}>{item[0]}</div> </td>
                            <td><div style={{width:'5vw',overflow:"hidden",height:height}}>{item[1]}</div></td>
                            <td><div style={{width:'20vw',overflow:'hidden',height:height}}>{item[2]}</div></td>
                            <td><input style={{width:'2vw'}} placeholder={item[4]} type='text'/></td>
                            <td><input style={{width:'2vw'}} placeholder={item[5]} type='text'/></td>
                            <td><input style={{width:'2vw'}} placeholder={item[6]} type='text'/></td>
                            <td><input style={{width:'2vw'}} placeholder={item[7]} type='text'/></td>
                            <td><input style={{width:'2vw'}} placeholder={item[8]} type='text'/></td>
                            <td><input style={{width:'4vw'}} placeholder={item[9]} type='text'/></td>
                            <td><textarea defaultValue={item[10]} maxLength={50} style={{width:'20vw',overflow:'hidden',height:height}}></textarea></td>
                            <td><input style={{width:'2vw'}} placeholder={item[11]} type='text'/></td>
                            <td><input style={{width:'2vw'}} vplaceholder={item[12]} type='text'/></td>
                        </tr>
            </>
            )
        }
        else if(component_state===2){
            return(
                <>
                <input placeholder={item[0]}  type='text'/>
                <input placeholder={item[1]} type='text'/>
                <input placeholder={item[2]} type='text'/>
                <input placeholder={item[3]} type='text'/><br/>
                </>
            )

        }
        else{
            return(
                <>
                <input placeholder={item[0]}  type='text'/>
                <input placeholder={item[1]} type='text'/><br/>
                </>
            )

        }


}
