import { useState } from "react";
import { admin_sidebar } from "../api";
const Group_change=()=>{
    const [value,set_value]=useState()
    const [style,set_style]=useState()
    return(
        <>
            <div>
                <select style={{display:style}} onChange={(e)=>{
                    e.target.value==='新增組別'
                    ?set_style('none')
                    :set_style('block')
                    set_value(e.target.value)}}>
                    {
                        admin_sidebar.map((item,index)=>{
                            return(
                                index>0 &&index<admin_sidebar.indexOf('離職員工')
                                ?<option>{item}</option>
                                :<></>
                            )
                        })
                    }
                    <option>新增組別</option>

                </select>
                <input style={{display:style==='block'}} onInput={(e)=>set_value(e)} placeholder='新增組別' type='text'/>
            </div>
            <div>


            </div>
        </>
    )
}
export default Group_change;
