import { useParams } from "react-router-dom"
import { Return_component } from "./data_input"

const New_performance=({synchronize_update})=>{

    async function out(e){
        var value=e.target.name//have two name 'cancel' or 'create'

        if(value==='create'){
            synchronize_update(true)
            //await api request==>return result to tell you success or failed
            setTimeout(() => {
                synchronize_update(false)
            }, 1000);
        }
    }

    const year=['請選擇年份']
    var Today=new Date();
    for(let i=0;i<3;i++){
        year.push(Today.getFullYear()-1911+i)
    }
    const {page}=useParams()
    var allEmp=JSON.parse(window.sessionStorage.getItem('banch_index'))[page]
    return(

    <div className="popup">

        <div style={style}>
            <div>
                <select>
                    {
                        year.map((item,index)=>{
                            return(<option key={index}>{index!==0?`民國${item}`:item}</option>)
                        })
                    }

                </select>
            </div>

            <Return_component number={5} page={page} arr_params={allEmp}/>


            <div style={{display:'flex',alignItems:'center',justifyContent:'center',zIndex:100000}}>
                <button name="cancel" onClick={out}>取消</button>

                <button name="create" onClick={out}>確認新增</button>
            </div>
        </div>
    </div>
    )
}
export default New_performance;
