import { useParams } from "react-router-dom"
import { Return_component } from "./data_input"

const Popup=({display,setDisplay})=>{
    const style={
        height:display.height,
        width:display.width,
        display:display.display,

        backgroundColor:display.backgroundColor,

        opacity:display.opacity,

        transition:display.transition,
        transform:display.transform,    
    }
    function out(){
        setDisplay({...display,
            height:'0%',
            width:'0%',
                    })
        setTimeout(()=>{
            setDisplay({...display,display:'none'})
        },300)
    }

    const year=['請選擇年份']
    var Today=new Date();
    for(let i=-5;i<5;i++){
        year.push(Today.getFullYear()-1911+i)
    }
    const {page}=useParams()
    var allEmp=JSON.parse(window.sessionStorage.getItem('banch_index'))[page]
    return(

    <div className="popup">

        <div style={style}>
            <div>
                <select>
                    {
                        year.map((item,index)=>{
                            return(<option>民國{item}</option>)
                        })
                    }

                </select>
            </div>
            <div style={{maxHeight:'50%'}}>
            <Return_component number={5} page={page} arr_params={allEmp.concat(allEmp)}/>
            </div>

            <div style={{display:'flex',alignItems:'center',justifyContent:'center',zIndex:100000}}>
                <button 
                    onClick={out}>取消</button>

                <button 
                    onClick={out}>確認新增</button>
            </div>
        </div>
    </div>
    )
}
export default Popup;
