import { Fragment } from "react"
import { useState,useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {login,select_route} from './api'

const Login=()=>{
    var token=window.sessionStorage.getItem('token')
    const [login_data,set_login_data]=useState({
        account:'',
        password:''
    })
    const [style1,set_style]=useState({
        style1:{opacity:0,transition:''},
        style2:{opacity:0,transition:''},
    })
    const navigate=useNavigate()
    console.log(login_data)
    useEffect(()=>{
        if(token){select_route(navigate)}
        set_style({...style,style1:{opacity:1,transition:'2s'}})
        var timer=setInterval(() => {
            set_style({...style,style2:{opacity:1,transition:'2s'}})
            clearInterval(timer)
        },3000);
    },[])


    const key_press_enter=(event)=>{
        if(event.key==="Enter"||event.key==="enter"){
            login(login_data,navigate)
        }
    }

    return(
        <Fragment>
            <div className="loginbox">
            <div onKeyDown={(event)=>key_press_enter(event)} className="login">
                <p style={{marginBottom:'8vh'}}>登入</p>
                <input style={style.style2} value={login_data.account} onChange={(e)=>set_login_data({...login_data,account:e.target.value})} type="text" placeholder="請輸入帳號"/><br/>

                <input style={style.style1} value={login_data.password} onChange={(e)=>set_login_data({...login_data,password:e.target.value})} type="password" placeholder="請輸入密碼"/><br/>
                <button onClick={()=>{

                        login(login_data,navigate)
                    }} 
                    style={{marginTop:'3vh'}} >登入</button>
            </div>
            </div>

        </Fragment>
    )
}
export default Login;
