                <div style={{width:'5vw'}}><div>{fixed_title[1]}</div></div>
                {/* 
                <div style={{width:'30vw',overflow:'hidden'}}>{fixed_title[2]}</div>
                */}
                <div style={{width:'2vw'}}>{fixed_title[3]}</div>
                <div style={{width:'2vw'}}>{fixed_title[12]}</div>
            </div>    
        )
    }
    else if(component_state===3){
        return(
            <div className={'row_container2'} id={'fixed_title'}>
                <div style={{width:'10vw'}}><div>{fixed_title[0]}</div></div> 
                <div style={{width:'10vw'}}><div>{fixed_title[1]}</div></div>
            </div>
        )
    }
    else if(component_state===4){
        return(
            <div className={'row_container'} id={'fixed_title'}>
                <div style={{width:'2vw'}}><div>{fixed_title_emp[0]}</div></div> 
                <div style={{width:'10vw'}}><div>{fixed_title_emp[1]}</div></div>
                <div style={{width:'3vw'}}><div>{fixed_title_emp[2]}</div></div>
                <div style={{width:'10vw'}}><div>{fixed_title_emp[3]}</div></div>
                <div style={{width:'10vw'}}><div>{fixed_title_emp[4]}</div></div>
                <div style={{width:'2vw'}}><div>{fixed_title_emp[5]}</div></div>
                <div style={{width:'10vw'}}><div>{fixed_title_emp[6]}</div></div>
                <div style={{width:'3vw'}}><div>{fixed_title_emp[7]}</div></div>
            </div>
        )
    }
    else if(component_state===5){
        return(
            <div className={'row_container2'} id={'fixed_title'}>
                <div style={{width:'10%'}}><div>{fixed_title_emp[0]}</div></div> 
                <div style={{width:'10%'}}><div>{fixed_title_emp[1]}</div></div>
                <div style={{width:'10%'}}>新增</div>
                <div style={{width:'10%'}}>不新增</div>
            </div>
        )

    }
};
