import axios from "axios"
import { Route } from "react-router-dom";
import Backend from "./backend";
import { useEffect } from "react";
export const config={
    url:'http://localhost:5000/'
};
export const article_bar_arr=['首頁','年度考核分數','每月考核績效']

export const log_out=['登出']
export const personal_sidebar=['個人年度目標','每月考核績效','年度考核分數'];
export const admin_sidebar=['保育組','公關組','社工組'];
export function manager_sidebar(){
    var banch=window.sessionStorage.getItem('banch');
    const manager= [banch];
    return manager
}


export function select_emp_name_id(number){
    var token=window.sessionStorage.getItem('token')


    const emp_arr=[]
    if(number===1){
        for(let i=0;i<40;i++){
            emp_arr.push([i,'name: '+i,'goal'+i,'attitude','efficiency','professional',2000+i,'directions'+i,i,0,0])
        }
        return(emp_arr)
    }
    else if(number===2){
        for(let i=0;i<40;i++){
            emp_arr.push([i,'name: '+i,'goal'+i,80+i])
        }
        return(emp_arr)

    }
    else{
        for(let i=0;i<40;i++){
            emp_arr.push([i,'name: '+i])
        }
        return(emp_arr)
    }



}

export function select_route(navigate){

    var permession=window.sessionStorage.getItem('permession')
    if(permession==="admin"){

      navigate(`/backend/admin/${admin_sidebar[0]}`)
    }
    else if(permession==="manager"){
        console.log("manager_siderbar()",manager_sidebar())
        navigate(`/backend/manager/${manager_sidebar()[0]}`)
    }
    else if(permession==='personal'){
        console.log()
        navigate(`/backend/personal/${personal_sidebar[0]}`)
    }
    else{navigate('/')}
  }



export async function backend(token){
    await axios({
        method:'POST',
        url:'http://localhost:5000/backend',
        headers:{
            'token':token,
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin':'*'
        }
    }).then((response)=>{
        console.log(response.data)

    }).catch((error)=>{

        console.log(error)
        alert('token error')
        return false

    })
}

export async function login(login_data,navigate,set_index_state){
    await axios({
        method:'POST',
        url:config.url,
        data:{
            'account':login_data.account,
            'password':login_data.password
        },
        headers:{
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin':'*'
        }
    }).then((response)=>{
        var data=response.data

        console.log(data)
        window.sessionStorage.setItem('token',data.token)
        window.sessionStorage.setItem('user_name',data.user_name)
        window.sessionStorage.setItem('banch',data.banch)
        window.sessionStorage.setItem('permession',data.permession)
        var token=window.sessionStorage.getItem('token')
        backend(token)
        console.log(set_index_state)
        select_route(navigate)
        return true

    }).catch((error)=>{
        console.log(error)
        alert('帳號或密碼輸入錯誤')
        return false

    })
}
