import webSocket from 'socket.io-client'
import { socketUrl } from './api';

let socket;
export const connectSocket = (token) => {
    if (socket?.connected) return;

    socket = io.connect(socketUrl.url, {
        transports: ['websocket'],
        autoConnect: true,
        forceNew: true,
        query: {
          'x-token': token,
        },
      });


    socket.on('connect', () => {
        console.log('socket connect');
    });
}
