import { useState,useEffect } from "react"
import { useParams } from "react-router-dom"
import { admin_sidebar } from "../api"

export function search_bar(page,article_page,sea){

    if(admin_sidebar.indexOf(page)===-1){
        return(
            <>            
                <Search_year/>

            </>      
        )
    }else{
        if(article_page==='年度考核分數'){
            return(
                <>            
                    <Search_name/>
                    <Search_year/>

                </> 
            )
        }
        else{
            return(
                <>            
                    <Search_name/>
                    <Search_year/>
                    <Search_month/>
                </>      
            )
        }
    }


}




export function Search_month(){
    const [value,set_value]=useState()
    const [style,set_style]=useState({
        button_display:{display:'inline-block'},
        input_display:{display:'none'},
        text:'請輸入月份'
    })

    return(
        <>
        <button style={style.button_display}  
                                        onClick={()=>
                                            set_style({...style,
                                                button_display:{display:'none'},
                                                input_display:{display:'inline-block'},
                                                text:''})}>
            {style.text}
        </button>
            <input  placeholder='月份:xx月'
                    value={value}
                    onChange={(e)=>{set_value(e.target.value)}}
                    style={style.input_display} 
                    className="search_input"
                    type="number" 
                    min="1" max="12"
                    step="1" 
                     />

        </>


    )
}



export function Search_year(){
    const [value,set_value]=useState()
    const [style,set_style]=useState({
        button_display:{display:'inline-block'},
        input_display:{display:'none'},
        text:'請輸入年份'
    })

    return(
        <>
        <button style={style.button_display}  
                                        onClick={()=>
                                            set_style({...style,
                                                button_display:{display:'none'},
                                                input_display:{display:'inline-block'},
                                                text:''})}>
            {style.text}
        </button>
            <input 
                placeholder="民國:xxx"
                value={value}
                onChange={(e)=>{set_value(e.target.value)}}
                style={style.input_display} 
                className="search_input"
                type="number"
                min="1900" 
                max="3099" step="1" 
                />
        </>

    )
}


export function Search_name(){
    const [emp_name,set_emp_name]=useState([])
    const [style,set_style]=useState({
        button_display:{display:'inline-block'},
        input_display:{display:'none'},
        text:'請選擇組員'
    })

    const {page}=useParams()

    useEffect(() => {
        const name_arr=['請選擇組員']
        var banch_index=JSON.parse(window.sessionStorage.getItem('banch_index'))
        for(const data of banch_index[page]){

            name_arr.push(data[2])
            console.log(data[2])
        }
        console.log('banch_index',banch_index[page])
        set_emp_name(name_arr)
    }, [page]);

    return(
    <>
        <button style={style.button_display}
        onClick={()=>
            set_style({...style,
                button_display:{display:'none'},
                input_display:{display:'inline-block'},
                text:''})}>
        {style.text}
        </button>

        <select style={style.input_display}>

                {
                    emp_name
                    ?emp_name.map((item,index)=>{
                        return(
                            <option key={index}>{item}</option>
                        )

                    })
                    :<></>
                }


        </select>
    </>
    )
}
