import { Fragment } from "react"
import { useState,useEffect } from "react";
import { useNavigate, useParams,Link } from "react-router-dom";
import { personal_sidebar } from "../api";
import { admin_sidebar } from "../api";
import { manager_sidebar } from "../api";
import Banch_page from "../article_page/banch"
import performance_per_month from "../article_page/performance_per_month"
import Public_relation from "../article_page/public_relation"
import Social_worker from "../article_page/scioal_worker"
import Personal_goal from "../article_page/personal_goal";
import Performance_per_month from "../article_page/performance_per_month";
import Performance_per_year from "../article_page/performance_per_year";
const Article=()=>{

    const navigate=useNavigate()
    const {page}=useParams()
    const {article_page}=useParams()
    var permession=window.sessionStorage.getItem('permession')
    useEffect(()=>{
        if(page==="登出"){
            window.sessionStorage.clear()
            navigate('/')
        }
    })



    function article_bar(){
        if(permession!=='personal' && admin_sidebar.indexOf(page)!==-1){
            return(
                <div className="article_banch_bar">
                <Link to={`/backend/${permession}/${page}`}>首頁</Link>
                <Link to={`/backend/${permession}/${page}/年度考核分數`}>年度考核分數</Link>
                <Link to={`/backend/${permession}/${page}/每月考核績效`}>每月考核績效</Link>
                </div>
            )
        }
    }
    function select_article_page(){
        if(article_page==='個人年度目標'){
            return(<Personal_goal/>)
        }else if(article_page==='每月考核績效'){
            return(<Performance_per_month/>)
        }else if(article_page==='年度考核分數'){
            return(<Performance_per_year/>)
        }
    }
    return(
        <Fragment>
            <div className="article">
                {
                    article_bar()
                }
                {
                    select_article_page()
                }
            </div>


        </Fragment>
    )
}
export default Article;
