import { useParams } from "react-router-dom";
import { admin_sidebar,backend } from "../api";
import { useEffect,useState } from "react";
import  {Return_component}  from "../backend_component/data_input";
import {Search_year,Search_name,Search_month} from "../backend_component/search_input";
import { Loading } from "../backend_component/loading";
import { sequential_search } from "../ algorithm/sequential_search";
const Performance_per_month=()=>{
    var token=window.sessionStorage.getItem('token')
    var permession=window.sessionStorage.getItem('permession')
    const [search,set_search]=useState({
        name:'',
        year:undefined,
        month:undefined,
    })

    const [page_data_arr,set_page_data_arr]=useState()

    const {page}=useParams()
    const {article_page}=useParams()

    useEffect(()=>{
        dataJSON.parse(window.sessionStorage.getItem('data'))[page]
        set_page_data_arr()
    },[])
    useEffect(()=>{
        console.log('search',search)
        if(page_data_arr!==undefined){
            var result=sequential_search(page_data_arr,1,search.name)
            console.log(result)
            set_page_data_arr(result)
        }

    },[search])

    if(!page_data_arr){
        return(<Loading/>)
    }
    function bar(){
        if(admin_sidebar.indexOf(page)===-1){
            return(
                <> 
                    <Search_year search={search} set_search={set_search}/>
                    <Search_month search={search} set_search={set_search}/>
                </>
            )
        }
        else{
            return(
            <>
                <Search_name search={search} set_search={set_search}/>
                <Search_year search={search} set_search={set_search}/>
                <Search_month search={search} set_search={set_search}/>
            </>
            )
        }
    }

    return(
        <>
        <div className="search_bar">
            <div>
                {
                    bar()
                }
            </div>
        </div>


            <Return_component number={1} page={page} arr_params={page_data_arr} />




        </>

    )
}
export default Performance_per_month;
