export function chinese_word_to_none(v){

    var temp=""
    //
    var char
    for(let i=0;i<v.length;i++){
        char=v.charCodeAt(i)
        if(char>0&&char<255 &&char!==32){
            temp+=v.charAt(i)
        }else{
            window.confirm('只有姓名能接受中文輸入')
        }
    }
    return temp
}
