import Article from "./backend_component/article";
import Sidebar from "./backend_component/sidebar";
import Title from "./backend_component/title";
import { admin_sidebar,log_out, personal_sidebar,article_bar_arr } from "./api";
import { useParams,useNavigate } from "react-router-dom";
import { useCallback, useEffect,useState } from "react";
import { useRef } from "react";
const Admin=()=>{
    const synchronize_update=useRef(false)//設定同步頁面
    const {page}=useParams()
    const {article_page}=useParams()
    const navigate=useNavigate()
    const combine=admin_sidebar.concat(log_out)
    const [data,set_data]=useState([]);
    const identifyCallback=useCallback(()=>{
        function identify(){
            if(article_page===article_bar_arr[1]){
                set_data(JSON.parse(window.sessionStorage.getItem('year_performance'))[page])
            }
            else if(article_page===article_bar_arr[2]){
                set_data(JSON.parse(window.sessionStorage.getItem('data'))[page])
            }
            else if(page===combine[3]||page===combine[4]){
                set_data([])
            }
            else{
                set_data(JSON.parse(window.sessionStorage.getItem('banch_index'))[page])
            }
        }
        console.log('change')
        identify()
    },[synchronize_update,article_page,page])


    useEffect(()=>{
        console.log('success to load effect')

        var  timer=setInterval(() => {
            if(synchronize_update.current===true){
                identifyCallback()
                console.log(`if 執行\n\n\n\n`)
                    clearInterval(timer)
            }else if(synchronize_update.current===false){
                identifyCallback()
                console.log(`else if執行\n\n\n\n`)
                clearInterval(timer)
            }
        }, 100);
    },[page,article_page,identifyCallback])

    useEffect(() => {
        if(combine.indexOf(page)===-1){
            navigate('/access====>error2348138%@&EG&#@&#(#**$)HHefuh2')
        }
        else if(article_page){

            if(personal_sidebar.indexOf(article_page)===-1){
                navigate('/access====>error2348138%@&EG&#@&#(#**$)HHefuh2')
            }
            else if(article_page==='個人年度目標'){
                navigate('/access====>error2348138%@&EG&#@&#(#**$)HHefuh2')
            }
            else if(page==='組別變動'&&article_page!==undefined){
                navigate('/access====>error2348138%@&EG&#@&#(#**$)HHefuh2')
            }

        }
    });

    return(
    <>
    <div className="head">
        <Title sidebar_arr={admin_sidebar}/>
        <Sidebar sidebar_arr={combine} style={"lesssmall"} />
        <Sidebar sidebar_arr={combine} style={"small"} />
    </div>

    <div className="body">
        <Sidebar sidebar_arr={combine} style={"large"}/>
        <Article  data={data} synchronize_update={synchronize_update}/>
    </div>
    </>
    )
}
export default Admin;
