import { Fragment } from "react"
import { useState,useEffect } from "react";
import { useNavigate, useParams,Link } from "react-router-dom";

import { manager_sidebar,admin_sidebar,personal_sidebar,article_bar_arr } from "../api";
import Banch_page from "../article_page/banch"
import performance_per_month from "../article_page/performance_per_month"
import Public_relation from "../article_page/public_relation"
import Social_worker from "../article_page/scioal_worker"
import Personal_goal from "../article_page/personal_goal";
import Performance_per_month from "../article_page/performance_per_month";
import Performance_per_year from "../article_page/performance_per_year";
const Article=()=>{
    const [sidebar_onclick,set_sidebar_onclick]=useState({
        now_item:'',
        classname:''
    })



    const navigate=useNavigate()
    const {page}=useParams()
    const {article_page}=useParams()
    var permession=window.sessionStorage.getItem('permession')

    function sidebar_onclick_class(index){
        if(sidebar_onclick.now_item===article_bar_arr[index]){
            return 'sidebar_div_onclick'
        }
        else{
            return sidebar_onclick.classname
        }
    }

    useEffect(()=>{
        set_sidebar_onclick({sidebar_onclick,now_item:article_page})
    })



    function article_bar(){
        if(permession!=='personal' && admin_sidebar.indexOf(page)!==-1){
            return(
                <div className="article_banch_bar">
                    {
                        article_bar_arr.map((item,index)=>{
                            if(item==='首頁'){
                                return(
                                    <Link key={index} to={`/backend/${permession}/${page}`}>{item}</Link>
                                )
                            }
                            else{
                                return(
                                    <Link key={index} to={`/backend/${permession}/${page}/${item}`}>{item}</Link>
                                )
                            }

                        })
                    }


                </div>
            )
        }
    }
    function select_article_page(){
        if(article_page==='個人年度目標'){
            return(<Personal_goal/>)
        }else if(article_page==='每月考核績效'){
            return(<Performance_per_month/>)
        }else if(article_page==='年度考核分數'){
            return(<Performance_per_year/>)
        }



    }

    return(
        <Fragment>
            <div className="article">
                {
                    article_bar()
                }
                {
                    select_article_page()
                }
            </div>


        </Fragment>
    )
}
export default Article;
