import React, { useEffect,useRef,useState} from "react"
import { useParams } from "react-router-dom"
import { select_emp_name_id } from "../api"
import { Loading } from "./loading"



export function virtual_table(arr,itemSize,screenHeight,scrollTop){


}


export function Return_component({number,page,arr_params}){
    const arr=arr_params[page]
    const ele=useRef(null)
    const {article_page}=useParams()
    const itemSize=30
    const bufferSize=10
    const screenHeight=400
    const visibleCount=screenHeight/itemSize
    const arrHeight=itemSize*arr.length

    const [now_item,set_now_item]=useState()

    const [startIndex,setStartIndex]=useState(0)
    const [endIndex,setEndIndex]=useState(visibleCount)
    const [offset,setoffset]=useState(0)
    useEffect(()=>{
        console.log(startIndex,endIndex,offset)
    },[])
    useEffect(()=>{
        setoffset(startIndex*30)
    },[startIndex])

    function onScroll(e){
        var Top=ele.current.scrollTop
        var startidx=Math.floor(Top/itemSize)
        var endidx=startidx+visibleCount
        var startoffset=Top - (Top % itemSize);

        console.log(startIndex,endIndex,offset)

        let originStartIdx=startidx

        setStartIndex(Math.max(originStartIdx - bufferSize, 0))
        setEndIndex(Math.min(
            originStartIdx + visibleCount + bufferSize,
            arr.length - 1
        ))

    };


    function render_display(){
        const content = [];
        for(let i=startIndex;i<=endIndex;++i){
            content.push({
                index:i,
                Style:
                {
                    width: '100%',
                    height: itemSize+'px',
                    position: "absolute",
                    left: 0,
                    right: 0,
                    top: i * itemSize+'px',
                }
            }
            )

        }
        return content
    }





        return(                
            <>

                <div  ref={ele} 
                    onScroll={onScroll} 
                    style={(arr&&arrHeight>screenHeight)||(arr&&arr.length===0)
                        ?{height:`${screenHeight}px`}
                        :{height:`${0}px`}}
                    className="all_employee">

                <div style={{height:`${arrHeight}px`}} 
                    className="employee">

                    <div 

                        className="employee_container">

                                {
                                    arr instanceof Array && arr.length>0
                                    ? arr.slice(startIndex,endIndex).map((item,index)=>{
                                        return(
                                            <>
                                            <Id_name 
                                            style={render_display()[index].Style}
                                            startIndex={startIndex}
                                            set={set_now_item}
                                            now_item={now_item}
                                            component_state={number} 
                                            key={index} 
                                            item={item} 
                                            index={index} />
                                            </>
                                        )
                                    })
                                    :<></>
                                }
                    </div>
                </div>
                </div>

            </>)
}

export function Id_name({set,now_item,item,index,component_state,style,startIndex}){
    const [zindex,setZindex]=useState('2vh')

    useEffect(()=>{
        if(now_item===index){
            setZindex('')
        }
        else{
            setZindex('100')
        }
    },[now_item])
        if(component_state===1){
            return(
                <>

                            <div className="row_container" onClick={()=>{set(index)}} style={style}>


                                <div style={{width:'1vw'}}>{item[0]}</div> 
                                <div style={{width:'5vw',overflow:"hidden",zIndex:zindex}}>{item[1]}</div>
                                <div style={{width:'20vw',overflow:'hidden',zIndex:zindex}}>{item[2]}</div>
                                <input style={{width:'2vw'}} placeholder={item[4]} type='text'/>
                                <input style={{width:'2vw'}} placeholder={item[5]} type='text'/>
                                <input style={{width:'2vw'}} placeholder={item[6]} type='text'/>
                                <input style={{width:'2vw'}} placeholder={item[7]} type='text'/>
                                <input style={{width:'2vw'}} placeholder={item[8]} type='text'/>
                                <input style={{width:'4vw'}} placeholder={item[9]} type='text'/>
                                <textarea defaultValue={item[10]} maxLength={50} style={{width:'20vw',overflow:'hidden',height:height}}></textarea>
                                <input style={{width:'2vw'}} placeholder={item[11]} type='text'/>
                                <input style={{width:'2vw'}} vplaceholder={item[12]} type='text'/>

                            </div>

                </>
            )
        }
        else if(component_state===2){
            return(
                <>
                <input placeholder={item[0]}  type='text'/>
                <input placeholder={item[1]} type='text'/>
                <input placeholder={item[2]} type='text'/>
                <input placeholder={item[3]} type='text'/><br/>
                </>
            )

        }
        else{
            return(
                <>
                <input placeholder={item[0]}  type='text'/>
                <input placeholder={item[1]} type='text'/><br/>
                </>
            )

        }


}
