import React, { useCallback, useEffect,useRef,useState} from "react"
import { useNavigate, useParams } from "react-router-dom"


import { fixed_title,fixed_title_emp,position_arr } from "../api"





export function Return_component({page_data_arr,set_page_data_arr,number,page,arr_params,set_new_emp}){

    const itemSize=30
    const bufferSize=20
    var screenHeight=0
    number===5
    ?screenHeight=200
    :screenHeight=500
    const visibleCount=screenHeight/itemSize
    const fixed_title_size=40

    const ele=useRef(null)

    const arr=arr_params
    const {article_page}=useParams()


    const arrHeight=itemSize*arr.length

    const [now_item,set_now_item]=useState()


    const [startIndex,setStartIndex]=useState(0)
    const [endIndex,setEndIndex]=useState(visibleCount)
    const [offset,setoffset]=useState(0)

    const [itemOffset,setItemoffset]=useState(0)

    useEffect(()=>{

        setoffset(startIndex*30)
    },[startIndex])
    useEffect(()=>{
        setStartIndex(0)
        setEndIndex(Math.min(
            visibleCount + bufferSize,
            arr.length
        ))
    },[arr,page])
    function onScroll(e){
        var Top=ele.current.scrollTop
        var startidx=Math.floor(Top/itemSize)
        var endidx=startidx+visibleCount
        var startoffset=Top - (Top % itemSize);

        console.log(startIndex,endIndex,offset)

        let originStartIdx=startidx

        setStartIndex(Math.max(originStartIdx - bufferSize, 0))
        setEndIndex(Math.min(
            originStartIdx + visibleCount + bufferSize,
            arr.length
        ))
        console.log(arr.length)

    };  

    function render_display(startidx,endidx,now_item,itemOffset){
        const content = [];
        for(let i=startidx;i<=endidx;++i){
            var Height=''
            var Zindex=0
            var Bgcolor=''
            var Boxshadow=''
            var Color=''
            var Top=i * itemSize+fixed_title_size+'px'
            if(now_item===i){

                Height=''
                Zindex=15
                Bgcolor='rgb(255,255,255)'
                Boxshadow='2px 2px 10px rgb(112,128,144)'
                Color='rgb(0,0,0)'
            }
            else{
                Height=itemSize+'px'
                Zindex=''
                Bgcolor=''
                Boxshadow=''
                Color=''
            }
            if(i>now_item){

                Top=itemOffset+i * itemSize+fixed_title_size+'px'
            }

            content.push({
                index:i,
                Style:
                {
                    transition:'0.3s',
                    boxShadow:Boxshadow,
                    height:Height,
                    backgroundColor:Bgcolor,
                    zIndex:Zindex,
                    color:Color,


                    position: "absolute",

                    left: 0,
                    right: 0,
                    top: Top
                }
            })
        }
        return content
    }
    return(                
        <div  
            ref={ele} 
            onScroll={onScroll} 
            style={(arr&&arrHeight>screenHeight)||(arr&&arr.length===0)
                    ?{height:`${screenHeight}px`}
                    :{height:`${screenHeight}px`}}
            className="all_employee">
        <div 
            style={article_page!=='每月考核績效'&&page!=='每月考核績效'
                    ?{height:`${arrHeight+itemOffset+fixed_title_size}px`,width:'100%'}
                    :{height:`${arrHeight+itemOffset+fixed_title_size}px`,width:'900px'}}
            className="employee">
            <Fixed_title component_state={number}/>
            <div className="employee_container">
                {
                    arr instanceof Array && arr.length>0
                    ? arr.slice(startIndex,endIndex).map((item,index)=>{
                        var style_content=render_display(startIndex,endIndex,now_item,itemOffset)
                        var style_arr=style_content[index]
                        return(
                            <Id_name 
                            page_data_arr={page_data_arr}
                                set_new_emp={set_new_emp}
                                setItemoffset={setItemoffset}
                                item_index={style_arr.index}
                                style={style_arr.Style}
                                set_page_data_arr={set_page_data_arr}
                                set={set_now_item}
                                component_state={number}
                                key={style_arr.index}
                                item={item} />
                            )
                    })
                    :<></>
                }
                </div>
            </div>
        </div>
    )
}

export function Id_name({page_data_arr,set_page_data_arr,set,item,component_state,style,set_new_emp,item_index,setItemoffset}){
    var rowitem_ref=useRef(null)
    var timeout=useRef(null)
    var composition=useRef(false)
    const {page}=useParams()
    var data=JSON.parse(window.sessionStorage.getItem('banch_index'))
    const {navigate}=useNavigate()
    var permession=window.sessionStorage.getItem('permession')

    const value=useRef(
        {
            id:'',
            name:'',
            group:'',
            account:'',
            password:'',
            onWorkDay:'',
            position:'',
            workState:''
        }
    )
    useEffect(()=>{
        //console.log('value.current',value.current)
    },[page_data_arr])

    useEffect(()=>{


        if(component_state===4){
            console.log(`item:[${item}]    item_index:${item_index}`)
            value.current={
                account:item[0]==='+'?'':item[0]||'',
                password:item[1]||'',
                name:item[2]||'',
                group:item[3]||'',
                id:item[4]||'',
                onWorkDay:item[5]||'',
                position:item[6]||'',
                workState:'on'
            }
        }
    },[item,component_state])

    function autoSave(e){
        var values =e.target.value
        var name=e.target.name
        clearTimeout(timeout.current)
        set_new_emp('更改中')
        if(component_state===4&& !composition.current){
            if(name==='id'){
                e.target.value=chinese_word_to_none(values)
                values=e.target.value
                value.current.id=e.target.value
            }
            else if(name==='name'){
                value.current.name=values
            }
            else if(name==='group'){
                value.current.group=values
            }
            else if(name==='account'){
                e.target.value=chinese_word_to_none(values)
                values=e.target.value
                value.current.account=values
            }
            else if(name==='password'){
                e.target.value=chinese_word_to_none(values)
                values=e.target.value
                value.current.password=values
            }
            else if(name==='onWorkDay'){
                e.target.value=chinese_word_to_none(values)
                values=e.target.value
                value.current.onWorkDay=values
            }
            else if(name==='position'){
                value.current.position=values
            }
            else if(name==='workState')
        timeout.current= setTimeout(() => {
            set_new_emp('存取中...')
            console.log('useeffect',value.current)
            var list=search(page_data_arr,item_index,obj_to_arr(value.current))
            data[page]=list

            window.sessionStorage.setItem('banch_index',JSON.stringify(data))
            var new_data=JSON.parse(window.sessionStorage.getItem('banch_index'))[page]
            set_page_data_arr([...new_data,['+']])

            setTimeout(()=>{
                set_new_emp('已儲存變更')
            },500)
        }, 500)
        }
    }
    function get_clientHeight(){
        set(item_index)
        if(rowitem_ref.current.clientHeight){
            var timer=setInterval(() => {
                setItemoffset(rowitem_ref.current.clientHeight)
                clearInterval(timer)
            },100);
        }
    }
    function checked(e){

    }

    function composition_method(e){
        const isChrome = !!window.chrome && !!window.chrome.webstore

        console.log('e.target.value',e.target.value)
        if(e.type==='compositionstart'){
            composition.current=true
            console.log('e.type:',e.type)
            console.log('composition:',composition)
        }

        else if(e.type==='compositionend'){
            composition.current=false
            console.log('e.type:',e.type)
            console.log('composition:',composition)
            autoSave(e)
            if (!composition && isChrome) {
                // fire onChange
            }
        }
    }

    if(component_state===1){
        return(
            <div ref={rowitem_ref}
                className="row_container" 
                onClick={get_clientHeight}
