import {io,Manager} from 'socket.io-client';
import {ip} from '../config';
const socketUrl = {
    url: `http://${ip}:5002`,
    urlMain: '/main',
}
let socketManager = (token) =>( new Manager(socketUrl.url,{
    transports: ['websocket'],
    autoConnect: true,
    forceNew: true,
    query: `${token}siousiou`,
}))
let socketBasic = socketManager.socket('/');
let socketMain = socketManager.socket(socketUrl.urlMain);

export const connectBasicSocket = (token) => {
    if (socketBasic?.connected) return;

    // socketBasic = io.connect(socketUrl.url, {
    //     transports: ['websocket'],
    //     autoConnect: true,
    //     forceNew: true,
    //     query: `${token}siousiou`,
    // });

    socketBasic.on('connect', (data) => {
        console.log('connect data => ',data)
    });
}

export const disconnectSocket = () => {
    if (!socketBasic) return;
    socketBasic?.disconnect();
};

export const connectMainSocket =async (token) => {
    console.log("it is main socket")
    socketMain = io.connect(socketUrl.url + socketUrl.urlMain, {
        transports: ['websocket'],
        autoConnect: true,
        forceNew: true,
        query: `${token}siousiou`,
    });

    socketMain.on('DataBaseChange', (data) => {
        console.log('hihi data => ', data)
    });
}
