import { useState,useEffect } from "react"
import { select } from "react-cookies"
import { useParams } from "react-router-dom"
import { admin_sidebar } from "../api"
import Popup from "./popup"

const permession=window.sessionStorage.getItem('permession')
export function Search_month({search,set_search}){

    const [style,set_style]=useState({
        button_display:{display:'inline-block'},
        input_display:{display:'none'},
        text:'請輸入月份'
    })

    return(
        <>
        <button style={style.button_display}  
                                        onClick={()=>
                                            set_style({...style,
                                                button_display:{display:'none'},
                                                input_display:{display:'inline-block'},
                                                text:''})}>
            {style.text}
        </button>
            <input  placeholder='月份:xx'
                    onChange={(e)=>{set_search({...search,month:e.target.value})}}
                    style={style.input_display} 
                    className="search_input"
                    type="number" 
                    min="1" max="12"
                    step="1" />
        </>
    )
}



export function Search_year({search,set_search}){

    const [style,set_style]=useState({
        button_display:{display:'inline-block'},
        input_display:{display:'none'},
        text:'請輸入年份'
    })

    return(
        <>
        <button style={style.button_display}  
                onClick={()=>
                    set_style({...style,
                    button_display:{display:'none'},
                    input_display:{display:'inline-block'},
                    text:''})}>
            {style.text}
        </button>
        <input 
            placeholder="民國:xxx"

            onChange={(e)=>{set_search({...search,year:e.target.value})}}
            style={style.input_display} 
            className="search_input"
            type="number"
            min="1900" 
            max="3099" step="1" />
        </>
    )
}



export function Search_name({search,set_search}){
    const [emp_name,set_emp_name]=useState([])
    const [style,set_style]=useState({
        button_display:{display:'inline-block'},
        input_display:{display:'none'},
        text:'請選擇組員'
    })

    const {page}=useParams()

    useEffect(() => {
        const name_arr=['請選擇組員']
        var banch_index=JSON.parse(window.sessionStorage.getItem('banch_index'))
        for(const data of banch_index[page]){
            permession==='admin'
            ?name_arr.push(data[2])
            :name_arr.push(data[1])
        }
        console.log('banch_index',banch_index[page])
        set_emp_name(name_arr)
    }, [page]);

    return(
    <>
        <button style={style.button_display}
        onClick={()=>
            set_style({...style,
                button_display:{display:'none'},
                input_display:{display:'inline-block'},
                text:''})}>
        {style.text}
        </button>

        <select onChange={(e)=>set_search({...search,name:e.target.value})}  style={style.input_display}>
                {
                    emp_name
                    ?emp_name.map((item,index)=>{
                        return(
                            <option  key={index}>{item}</option>
                        )
                    })
                    :<></>
                }
        </select>
    </>
    )
}
export function New_employee({set_new_emp}){
    const [a,set_a]=useState('新增組員')
    useEffect(()=>{
        var timer=setInterval(() => {
            if(a==='儲存中...'){
                set_a('新增組員')
                clearInterval(timer)
                alert('新增成功')
            }
        },500);
    },[a])


    return(
        <>
            <button onClick={()=>{
                if(a==='新增組員'){
                    set_a('儲存')
                }else{
                    set_a('儲存中...')
                }

                set_new_emp(true)}}>
                {
                    a
                }
            </button>
        </>
    )
}
export function New_year(){
    const [display,setDisplay]=useState(
        {
            display:'none',
            backgroundColor:'rgba(115,122,224,0)',
            height:'0vh',
            width:'0vw',
            transform:'',
            transition:'',
        }
    )
    return(
        <>
            <button onClick={()=>{
                setDisplay({
                    display:'block',
                    height:'50vh',
                    width:'50vw',
                    backgroundColor:'rgba(255,248,220,0.8)',
                    transform:'scale(1.5,1.5)',
                    transition:'1s',
                })
                }}>

                新增年度績效    
            </button>
            <Popup setDisplay={setDisplay} display={display}/>
        </>
    )
}
