import {search_bar} from "../backend_component/search_input";
import  {Return_component}  from "../backend_component/data_input";
import { useParams } from "react-router-dom";
import { useEffect,useState } from "react";
import { backend } from "../api";
import { Loading } from "../backend_component/loading";
const Performance_per_year=()=>{
    var token=window.sessionStorage.getItem('token')
    var permession=window.sessionStorage.getItem('permession')
    const {page}=useParams()
    const {article_page}=useParams()
    const [page_data_arr,set_page_data_arr]=useState()
    function add_every_year(){
        var content=[]
        var current_year=0
        var current_employee=''
        var emp_arr=[]
        var attitude=0
        var efficiency=0
        var performance=0
        for(var employee_data in page_data_arr[page]){
            if(employee_data[3]===current_employee){
                if(employee_data[4]===current_year){
                    attitude+=employee_data[6]
                    efficiency+=employee_data[7]
                    performance+=employee_data[8]
                }
                else{
                    emp_arr.push
                    current_year=employee_data[4]
                }
            }
            else{
                current_employee=employee_data[3]
                emp_arr.push(employee_data[0],employee_data[1],employee_data[2])
            }
        }


    }

    useEffect(()=>{
        set_page_data_arr(JSON.parse(window.sessionStorage.getItem('data')))


    },[])

        if(!page_data_arr){
            return(<Loading/>)
        }
    return(
        <>
        <div className="search_bar">
            <div>
                {
                    search_bar(page,article_page)
                }
            </div>
        </div>
        <Return_component number={2} page={page} arr_params={page_data_arr} />
        </>
    )
}
export default Performance_per_year;
