import { Fragment } from "react"
import { useState,useEffect } from "react";
import { useNavigate,Link,NavLink, useParams } from "react-router-dom";

const Sidebar=({sidebar_arr,style})=>{
    const {page}=useParams()
    const [sidebar_onclick,set_sidebar_onclick]=useState({
        now_item:'',
        classname:''
    })
    var permession=window.sessionStorage.getItem('permession')
    function sidebar_onclick_class(index){
        if(sidebar_onclick.now_item===sidebar_arr[index]){
            return 'sidebar_div_onclick'
        }
        else{
            return sidebar_onclick.classname
        }
    }
    useEffect
    console.log("page",sidebar_onclick.now_item)

    return(
        <Fragment>
            <div className={style}>
                {
                    sidebar_arr.map((item,index)=>(
                        <Link key={index}  to={`/backend/${permession}/${item}`} >
                            <div className={sidebar_onclick_class(index)}
                                 onClick={()=>
                                    set_sidebar_onclick({...sidebar_onclick,now_item:sidebar_arr[index]})} >{item}</div>
                        </Link>
                        )
                    )
                }
            </div>


        </Fragment>
    )
}
export default Sidebar;
