import { useParams } from "react-router-dom"
import { Return_component } from "./data_input"
import { Loading } from "./loading"
import { useState,useEffect } from "react"
const New_performance=({data,synchronize_update,synchronize})=>{
    const {page}=useParams()
    const [page_data_arr,set_page_data_arr]=useState([])
    async function out(e){
        var value=e.target.name//have two name 'cancel' or 'create'

        if(value==='create'){
            synchronize_update(true)
            //await api request==>return result to tell you success or failed
            setTimeout(() => {
                synchronize_update(false)
            }, 1000);
        }
    }

    const year=['請選擇年份']
    var Today=new Date();
    for(let i=0;i<3;i++){
        year.push(Today.getFullYear()-1911+i)
    }
    useEffect(()=>{
        if(data.length!==0){
            set_page_data_arr(data)
        }
        else{
            set_page_data_arr([])
        }
    },[page,data])
    if(!page_data_arr||synchronize===true){
        return(<Loading/>)
    }
    return(
<>
                <select>
                    {
                        year.map((item,index)=>{
                            return(<option key={index}>{index!==0?`民國${item}`:item}</option>)
                        })
                    }

                </select>

            {
                page_data_arr.length!==0
                ?<Return_component 
                    synchronize_update={synchronize_update} 
                    number={5} 
                    page={page}
                    arr_params={data}/>
                :<></>
            }



            <div style={{display:'flex',alignItems:'center',justifyContent:'center',zIndex:100000}}>
                <button name="cancel" onClick={out}>取消</button>
                <button name="create" onClick={out}>確認新增</button>
            </div>

</>
    )
}
export default New_performance;
