export function bubble_sorting(arr,tar_position,direct){
    //desc 高到低
    //asc 低到高
    var len=arr.length
    if(direct==='desc'){
        while(len>1){
            for(let i=0;i<len;i++){

                if(arr[i][tar_position]<arr[i+1][tar_position]){
                    [arr[i],arr[i+1]]=[arr[i+1],arr[i]]
                }
            }
        }
    }
    else if(direct==='asc'){
        while(len>1){
            for(let i=0;i<len;i++){

                if(arr[i][tar_position]>arr[i+1][tar_position]){
                    [arr[i],arr[i+1]]=[arr[i+1],arr[i]]
                }
            }
        }
    }
    return arr
}
