import React, { useEffect,useRef,useState} from "react"
import { useParams } from "react-router-dom"
import { select_emp_name_id } from "../api"
import { Loading } from "./loading"



export function virtual_table(arr,itemSize,screenHeight,scrollTop){


}


export function Return_component({number,page,arr_params}){
    const arr=arr_params[page]
    const ele=useRef(null)
    const {article_page}=useParams()
    const itemSize=2
    const screenHeight=4

    const [now_item,set_now_item]=useState()
    const [scrollTop,setScrollTop]=useState(0)
    const [arrHeight,setArrHeight]=useState()
    const [visibleCount,setVisibleCount]=useState()
    const [startIndex,setStartIndex]=useState(0)
    const [endIndex,setEndIndex]=useState()
    const [visibleData,setVirtualData]=useState([])
    const [offset,setoffset]=useState()
    const [Style,setStyle]=useState()


    function onScroll(e){
        var scrollTop=ele.current.scrollTop
        var startidx=Math.floor(scrollTop/itemSize)
        var endidx=startidx+visibleCount
        var offset=scrollTop - (scrollTop % itemSize);
        if(endidx>=arr.length-1){


        }
        else{
            setVirtualData(arr.slice(startidx,endidx))
            console.log(arr.length,startidx,endidx)
            render_display(startidx,endidx)
        }
        setScrollTop(scrollTop)
        setStartIndex(startidx)
        setEndIndex(endidx)
        setoffset(offset)

    };
    function render_display(startidx,endidx){
        const content = [];
        for(let i=startidx;i<=endidx;++i){
            content.push({
                index:i,
                Style:(
                {
                    width: '100%',
                    height: itemSize+'vh',
                    position: "relative",
                    left: 0,
                    right: 0,
                    top: i * itemSize+'vh',


                }
            )}
            )

            setStyle(content)

        }
    }


    useEffect(()=>{
        var visible=Math.ceil(screenHeight/itemSize)
        var startidx=Math.floor(scrollTop/itemSize)
        var endidx=startIndex+visible

        setArrHeight(itemSize*arr.length)
        setVisibleCount(visible)
        setStartIndex(startidx)
        setEndIndex(endidx)
        setVirtualData(arr.slice(startidx,endidx))
        console.log(itemSize*arr.length,startidx,endidx)
        render_display(startidx,endidx)

    },[])

    if(visibleData){
        return(                
            <>
            <div ref={ele} onScroll={onScroll} className="all_employee">
                <div style={{minHeight:`${arrHeight}vh`}} className="employee">
                    <div class>
                                {
                                    visibleData.map((item,index)=>{
                                        return(
                                            <>
                                            <Id_name 
                                            style={Style[index].Style}
                                            set={set_now_item}
                                            now_item={now_item}
                                            component_state={number} 
                                            key={index} item={item} 
                                            index={index} />
                                            </>
                                        )
                                    })
                                }
                    </div>
                </div>
            </div>
            </>)
    }


}

export function Id_name({set,now_item,item,index,component_state,style}){
    const [height,set_height]=useState('2vh')

    useEffect(()=>{
        if(now_item===index){
            set_height('')
        }
        else{
            set_height('4vh')
        }
    },[now_item])
        if(component_state===1){
            return(
                <>

                            <div className="row_container" onClick={()=>{set(index)}} style={style}>
                                <div style={{width:'1vw'}}>{item[0]}</div> 
                                <div style={{width:'5vw',overflow:"hidden",height:height}}>{item[1]}</div>
                                <div style={{width:'20vw',overflow:'hidden',height:height}}>{item[2]}</div>
                                <input style={{width:'2vw'}} placeholder={item[4]} type='text'/>
                                <input style={{width:'2vw'}} placeholder={item[5]} type='text'/>
                                <input style={{width:'2vw'}} placeholder={item[6]} type='text'/>
                                <input style={{width:'2vw'}} placeholder={item[7]} type='text'/>
                                <input style={{width:'2vw'}} placeholder={item[8]} type='text'/>
                                <input style={{width:'4vw'}} placeholder={item[9]} type='text'/>
                                <textarea defaultValue={item[10]} maxLength={50} style={{width:'20vw',overflow:'hidden',height:height}}></textarea>
                                <input style={{width:'2vw'}} placeholder={item[11]} type='text'/>
                                <input style={{width:'2vw'}} vplaceholder={item[12]} type='text'/>
                            </div>

                </>
            )
        }
        else if(component_state===2){
            return(
                <>
                <input placeholder={item[0]}  type='text'/>
                <input placeholder={item[1]} type='text'/>
                <input placeholder={item[2]} type='text'/>
                <input placeholder={item[3]} type='text'/><br/>
                </>
            )

        }
        else{
            return(
                <>
                <input placeholder={item[0]}  type='text'/>
                <input placeholder={item[1]} type='text'/><br/>
                </>
            )

        }


}
