import { useState } from "react"

export function Search_month(){
    return(
        <input  className="search_input"
                type="number" 
                min="1" max="12"
                step="1" 
                placeholder="請輸入月份" />
    )
}



export function Search_year(){
    const [style,set_style]=useState({
        button_display:'block',
        input_display:'none',
        text:'請輸入年份'
    })
    return(
        <button style={style.button_display}  
                                        onClick={()=>
                                            set_style({...style,
                                                button_display:'none',
                                                input_display:'inline-block',
                                                text:''    })}>
            {style.text}
            <input style={{...style,input_display}} className="search_input"
                type="number"
                min="1900" 
                max="3099" step="1" 
                />
        </button>

    )
}


export function Search_name(){
    const name_arr=['請選擇員工','mark','phonbe','kobe']
    return(

        <select>

                {
                    name_arr.map((item,index)=>{
                        return(
                            <option key={index}>{item}</option>
                        )

                    })
                }


        </select>
    )
}
